mapper.select:
SELECT tasks_chofer_delivery.id AS task_chofer_delivery_id, tasks_chofer_delivery.created_at AS task_chofer_delivery_created_at, tasks_chofer_delivery.system_message AS task_chofer_delivery_system_message, status_almacen_tasks_catalog.description AS status_almacen_task_catalog_description, attend_almacen_tasks_type_catalog.description AS attend_almacen_task_type_catalog_description, renta.id_renta AS event_id, renta.folio AS folio, renta.fecha_evento AS event_date, renta.fecha_entrega AS delivery_date, renta.hora_entrega AS delivery_hour, renta.descripcion AS address_event, c.nombre AS customer_name, c.apellidos AS customer_last_name, estado.descripcion AS event_status, tipo.tipo AS event_type, usuarios.nombre AS chofer_name, usuarios.apellidos AS chofer_last_name, usuarios.id_usuarios AS chofer_id, renta.descuento AS descuento_renta, renta.iva AS iva_renta, renta.deposito_garantia AS deposito_garantia_renta, renta.envio_recoleccion AS envio_recoleccion_renta, ( SELECT SUM(a.abono) FROM abonos a WHERE a.id_renta = renta.id_renta ) AS total_abonos_renta, ( SELECT SUM(IF( ( f.fg_devolucion = '0' AND f.fg_accidente_trabajo = '0'),(f.cantidad * f.precio_cobrar),0) )AS total FROM faltantes f INNER JOIN articulo a ON (f.id_articulo = a.id_articulo) WHERE f.id_renta = renta.id_renta AND f.fg_activo = '1' ) AS total_faltantes_renta, ( SELECT IF(porcentaje_descuento >= 0, (SUM( (cantidad*p_unitario) - ((cantidad*p_unitario) * (porcentaje_descuento / 100) ))), (SUM( (cantidad*p_unitario) ))) AS suma FROM detalle_renta WHERE id_renta = renta.id_renta ) AS sub_total_renta FROM tasks_chofer_delivery tasks_chofer_delivery INNER JOIN attend_almacen_tasks_type_catalog attend_almacen_tasks_type_catalog ON (tasks_chofer_delivery.attend_almacen_tasks_type_catalog_id = attend_almacen_tasks_type_catalog.id) INNER JOIN status_almacen_tasks_catalog status_almacen_tasks_catalog ON (tasks_chofer_delivery.status_almacen_tasks_catalog_id = status_almacen_tasks_catalog.id) INNER JOIN renta renta ON (renta.id_renta = tasks_chofer_delivery.renta_id) INNER JOIN tipo tipo ON (tipo.id_tipo = renta.id_tipo) INNER JOIN estado estado ON (estado.id_estado = renta.id_estado) INNER JOIN clientes c ON (c.id_clientes = renta.id_clientes) INNER JOIN usuarios usuarios ON (usuarios.id_usuarios = tasks_chofer_delivery.chofer_id) WHERE tasks_chofer_delivery.fg_active = '1'
AND renta.folio = #{folio}
AND STR_TO_DATE(renta.fecha_evento,'%d/%m/%Y') BETWEEN STR_TO_DATE(#{initEventDate},'%d/%m/%Y') AND STR_TO_DATE(#{endEventDate},'%d/%m/%Y')
AND STR_TO_DATE(renta.fecha_pedido,'%d/%m/%Y') BETWEEN STR_TO_DATE(#{initCreatedDate},'%d/%m/%Y') AND STR_TO_DATE(#{endCreatedDate},'%d/%m/%Y')
AND STR_TO_DATE(renta.fecha_entrega,'%d/%m/%Y') BETWEEN STR_TO_DATE(#{initDeliveryDate},'%d/%m/%Y') AND STR_TO_DATE(#{endDeliveryDate},'%d/%m/%Y')
#{item}
#{item}
AND CONCAT(c.nombre," ",c.apellidos) LIKE CONCAT('%',#{customer},'%')
AND tasks_chofer_delivery.chofer_id = #{driverId}
#{item}
ORDER BY tasks_chofer_delivery.id DESC LIMIT #{limit}
