mapper.select:
SELECT tasks_almacen.id AS task_almacen_id, tasks_almacen.created_at AS task_almacen_created_at, status_almacen_tasks_catalog.description AS status_almacen_task_catalog_description, attend_almacen_tasks_type_catalog.description AS attend_almacen_task_type_catalog_description, renta.id_renta AS event_id, renta.folio AS folio, renta.fecha_evento AS event_date, renta.fecha_entrega AS delivery_date, renta.hora_entrega AS delivery_hour, renta.descripcion AS address_event, c.nombre AS customer_name, c.apellidos AS customer_last_name, estado.descripcion AS event_status, tipo.tipo AS event_type, usuarios_by_category.nombre AS user_by_category_name, usuarios_by_category.apellidos AS user_by_category_last_name, usuarios_by_category.id_usuarios AS user_by_category_id FROM tasks_almacen tasks_almacen INNER JOIN attend_almacen_tasks_type_catalog attend_almacen_tasks_type_catalog ON (tasks_almacen.attend_almacen_tasks_type_catalog_id = attend_almacen_tasks_type_catalog.id) INNER JOIN status_almacen_tasks_catalog status_almacen_tasks_catalog ON (tasks_almacen.status_almacen_tasks_catalog_id = status_almacen_tasks_catalog.id) INNER JOIN renta renta ON (renta.id_renta = tasks_almacen.renta_id) INNER JOIN tipo tipo ON (tipo.id_tipo = renta.id_tipo) INNER JOIN estado estado ON (estado.id_estado = renta.id_estado) INNER JOIN clientes c ON (c.id_clientes = renta.id_clientes) INNER JOIN usuarios usuarios_by_category ON (usuarios_by_category.id_usuarios = tasks_almacen.user_by_category_id) WHERE tasks_almacen.fg_active = '1'
AND STR_TO_DATE(renta.fecha_entrega, '%d/%m/%Y') >= STR_TO_DATE(#{systemDate}, '%d/%m/%Y' )
AND renta.folio = #{folio}
AND STR_TO_DATE(renta.fecha_evento,'%d/%m/%Y') BETWEEN STR_TO_DATE(#{initEventDate},'%d/%m/%Y') AND STR_TO_DATE(#{endEventDate},'%d/%m/%Y')
AND STR_TO_DATE(renta.fecha_pedido,'%d/%m/%Y') BETWEEN STR_TO_DATE(#{initCreatedDate},'%d/%m/%Y') AND STR_TO_DATE(#{endCreatedDate},'%d/%m/%Y')
AND STR_TO_DATE(renta.fecha_entrega,'%d/%m/%Y') BETWEEN STR_TO_DATE(#{initDeliveryDate},'%d/%m/%Y') AND STR_TO_DATE(#{endDeliveryDate},'%d/%m/%Y')
#{item}
#{item}
AND CONCAT(c.nombre," ",c.apellidos) LIKE CONCAT('%',#{customer},'%')
AND tasks_almacen.user_id = #{userId}
#{item}
ORDER BY tasks_almacen.id DESC LIMIT #{limit}
